/* ===========================
   Base Sidebar
=========================== */
.sidebar {
  position: fixed;
  left: 0;
  display: inline-flex;
  height: calc(100vh - var(--header-height, 64px)); /* âœ… responsive height */
  padding: var(--units-unit-13, 52px) var(--units-unit-4, 16px);
  flex-direction: column;
  justify-content: flex-start; /* content starts from top */
  align-items: flex-start;
  gap: 10px;
  flex-shrink: 0;
  background: var(--colors-common-black, #000);
  transition: width 0.3s ease, padding 0.3s ease;
  width: 72px;
  overflow: hidden;
  z-index: 100;
  box-sizing: border-box;
}

/* ===========================
   Expanded Sidebar
=========================== */
.sidebar.expanded {
  width: 220px;
  height: calc(100vh - var(--header-height, 64px));
  padding: var(--units-unit-13, 52px) var(--units-unit-6, 24px);
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  gap: 10px;
  flex-shrink: 0;
  background: var(--colors-common-black, #000);
}

/* ===========================
   Top Section
=========================== */
.sidebar-top {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 24px;
  width: 100%;
}

.sidebar.expanded .sidebar-top {
  align-items: flex-start;
}

/* ===========================
   Logo Box
=========================== */
.logo-box {
  display: flex;
  width: 40px;
  height: 40px;
  padding: 8px;
  justify-content: center;
  align-items: center;
  gap: var(--units-unit-6, 24px);
  border-radius: 12px;
  background: radial-gradient(
    111.84% 132.86% at 59.99% 103.12%,
    var(--colors-base-primary, #0554EF) 0%,
    var(--colors-common-purple-500, #8937E2) 100%
  );
  cursor: pointer;
  transition: width 0.3s ease, padding 0.3s ease;
}

.sidebar.expanded .logo-box {
  display: flex;
  width: 100%;
  padding: 8px 16px;
  align-items: center;
  justify-content: flex-start;
  gap: var(--units-unit-4, 16px);
  align-self: stretch;
  border-radius: 12px;
  background: radial-gradient(
    111.84% 132.86% at 59.99% 103.12%,
    var(--colors-base-primary, #0554EF) 0%,
    var(--colors-common-purple-500, #8937E2) 100%
  );
}

.logo-box .logo-gradient {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 26px;
  height: 26px;
  flex-shrink: 0;
}

.logo-box .ask-label {
  color: #fff;
  font-size: 16px;
  font-weight: 600;
  letter-spacing: 0.01em;
}

/* ===========================
   Menu Section
=========================== */
.menu {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 24px;
  margin-top: 24px;
  width: 100%;
}

.sidebar.expanded .menu {
  align-items: flex-start;
}

/* ===========================
   Menu Item
=========================== */
.menu-item {
  display: flex;
  align-items: center;
  gap: 12px;
  font-size: 14px;
  color: #fff;
  cursor: pointer;
  border-radius: 8px;
  padding: 8px 10px;
  transition: background 0.2s ease, color 0.2s ease;
  width: 100%;
}

.menu-item:hover {
  background: rgba(255, 255, 255, 0.1);
}

/* SVG Icon Resize (Collapsed vs Expanded) */
.menu-item svg {
  width: 24px;
  height: 24px;
  aspect-ratio: 1 / 1;
  transition: all 0.25s ease;
}

.sidebar.expanded .menu-item svg {
  width: 18px;
  height: 18px;
  aspect-ratio: 1 / 1;
}

/* Text Label Visibility */
.menu-item span {
  opacity: 0;
  visibility: hidden;
  white-space: nowrap;
  transition: opacity 0.3s ease, visibility 0.3s ease;
}

.sidebar.expanded .menu-item span {
  opacity: 1;
  visibility: visible;
}

/* ===========================
   Bottom Section
=========================== */
.sidebar-bottom {
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  gap: 20px;
  margin-top: auto;
  position: relative; /* for toggle positioning */
}

/* ===========================
   Toggle Button
=========================== */
.toggle-btn {
  background: none;
  border: none;
  cursor: pointer;
  padding: 8px;
  border-radius: 50%;
  transition: background-color 0.3s ease;

  position: absolute;
  bottom: var(--units-unit-13, 52px); /* âœ… 52px from bottom */
  left: 50%;
  transform: translateX(-50%); /* center horizontally */
}

.toggle-btn:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

